import _isEmpty from "lodash/isEmpty";
export function getAreaData(data, maxDepth) {
    const { province_list, city_list, county_list } = data;
    const dataArray = [];
    if (maxDepth >= 1 && !_isEmpty(province_list)) {
        dataArray.push(province_list);
        if (maxDepth >= 2 && !_isEmpty(city_list)) {
            dataArray.push(city_list);
            if (maxDepth >= 3 && !_isEmpty(county_list)) {
                dataArray.push(county_list);
            }
        }
    }
    return dataArray;
}
